;s:0:\"\";s:7:\"ln_link\";s:39:\"https://www.linkedin.com/in/brianross87\";s:7:\"gp_link\";s:0:\"\";s:14:\"pinterest_link\";s:0:\"\";s:11:\"tumblr_link\";s:0:\"\";s:11:\"reddit_link\";s:0:\"\";s:9:\"image_uri\";s:67:\"http://advertisingbrothers.com/wp-content/uploads/2015/03/brian.jpg\";}i:5;a:13:{s:4:\"name\";s:20:\"Joseph J. Ross, Esq.\";s:8:\"position\";s:39:\"Cofounder / President / General Counsel\";s:11:\"description\";s:266:\"Joe has been making websites since before making websites was a thing. He has the project management and editorial experience to build powerful strategies. And those privacy policies and terms of service you see everywhere, you need those too, and he can write them.\";s:7:\"fb_link\";s:0:\"\";s:7:\"tw_link\";s:26:\"http://twitter.com/joeross\";s:7:\"bh_link\";s:0:\"\";s:7:\"db_link\";s:0:\"\";s:7:\"ln_link\";s:32:\"http://linkedin.com/in/atjoeross\";s:7:\"gp_link\";s:26:\"http://google.com/+joeross\";s:14:\"pinterest_link\";s:0:\"\";s:11:\"tumblr_link\";s:22:\"http://geekesquire.com\";s:11:\"reddit_link\";s:0:\"\";s:9:\"image_uri\";s:82:\"http://advertisingbrothers.com/wp-content/uploads/2015/03/Joe-Ross-full-square.png\";}s:12:\"_multiwidget\";i:1;}','yes'),(330,'limit_login_lockouts','a:0:{}','yes'),(331,'limit_login_logged','a:308:{s:14:\"176.102.38.151\";a:1:{s:7:\"joeross\";i:4;}s:15:\"195.154.242.151\";a:1:{s:43:\"advertisingbrothers@advertisingbrothers.com\";i:1;}s:13:\"73.160.254.28\";a:1:{s:5:\"bross\";i:1;}s:12:\"194.6.233.49\";a:2:{s:5:\"admin\";i:7;s:7:\"joeross\";i:9;}s:13:\"94.190.48.101\";a:1:{s:7:\"joeross\";i:3;}s:12:\"94.190.10.25\";a:1:{s:7:\"joeross\";i:5;}s:14:\"217.197.125.61\";a:1:{s:7:\"joeross\";i:3;}s:12:\"91.183.94.86\";a:2:{s:5:\"admin\";i:1;s:19:\"advertisingbrothers\";i:2;}s:12:\"46.189.245.8\";a:2:{s:5:\"admin\";i:1;s:19:\"advertisingbrothers\";i:1;}s:13:\"84.245.219.96\";a:2:{s:5:\"admin\";i:1;s:19:\"advertisingbrothers\";i:1;}s:14:\"186.105.109.93\";a:1:{s:5:\"admin\";i:1;}s:12:\"192.0.225.36\";a:1:{s:5:\